mapper.select:

select indickey,indicname,indicdes,freq,indicattr,indictype,facttable,savedays,callevel,flag,creator,create_dt,isjl,region_num,region_sf
 from mm11_indic 
 where flag='1' and indictype = #{indictype} and freq = #{freq}


select t1.indickey,indicname,indicdes,freq,indicattr,indictype,facttable,savedays,callevel,flag,creator,create_dt,isjl,region_num,region_sf
 from mm11_indic t1
 where indickey = #{indickey}
